            </div>
          </>
        )} */}
        {showError && (
          <div className="middlehalf">
            <div className="mainAlertBoxError" role="alert">
              <p className="alertBoxError">
                <b className="alertFont1">{messsage}</b>
              </p>
            </div>
          </div>
        )}
      </div>
    </>
  );
};

export default CallRecords;
